   // 2D linear interpolation to calculate rho(T,p) in kg/m^3 
   
   i_indexT = int(fabs(floor((T-T0)/dT)));
   i_indexp = int(fabs(floor((p-p0)/dp)));
   
   Temp_PS = T0+i_indexT*dT;
   Press_PS = p0+i_indexp*dp;
   
   rho_P1_PS = rho_PS[i_indexp][i_indexT]+(rho_PS[i_indexp][i_indexT+1]-rho_PS[i_indexp][i_indexT])*(T-Temp_PS)/dT;
   rho_P2_PS = rho_PS[i_indexp+1][i_indexT]+(rho_PS[i_indexp+1][i_indexT+1]-rho_PS[i_indexp+1][i_indexT])*(T-Temp_PS)/dT;
   
   rho_TP_PS = rho_P1_PS + (rho_P2_PS-rho_P1_PS)*(p-Press_PS)/dp;

   return rho_TP_PS;

